mapper.select:
SELECT IFNULL(t.`GNUM`, '') AS GNUM, IFNULL(t.`ITEM_NO`, '') AS ITEM_NO, IFNULL(s.`GNUM`, '') AS G_NO,
IFNULL(s.`G_CODE`, '') AS G_CODE, IFNULL(s.`G_NAME`, '') AS G_NAME, IFNULL(s.`G_MODEL`, '') AS G_MODEL, IFNULL(s.`BARCODE`, '') AS BAR_CODE,
IFNULL(s.`UNIT`, '') AS UNIT, IFNULL(s.`CURRENCY`, '') AS CURRENCY, IFNULL(t.`REFOUND_QTY`, '') AS QTY, IFNULL(t.`PRICE`, '') AS PRICE, IFNULL(t.`TOTAL`, '') AS TOTAL, IFNULL(t.`NOTE`, '') AS NOTE FROM t_import_order_detail t LEFT JOIN t_import_orders orders ON t.ORDERS_ID = orders.ORDERS_ID LEFT JOIN t_import_order_detail s ON (s.ORDERS_ID = t.ORDERS_ID) WHERE t.`ORDERS_ID` = #{orderId}

SELECT IFNULL(t.`APP_UID`, '') AS APP_UID, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'APP_UID' AND c.CODE = t.APP_UID) AS APP_UNAME, IFNULL(t.`COP_NO`, '') AS COP_NO, IFNULL(t.`CUSTOM_CODE`, '') AS CUSTOM_CODE, IFNULL(t.`CHK_CUSTOM_CODE`, '') AS CHK_CUSTOM_CODE, IFNULL(t.`APP_TYPE`, '') AS APP_TYPE, IFNULL(t.`APP_STATUS`, '') AS APP_STATUS, IFNULL(t.`APP_TIME`, '') AS APP_TIME, IFNULL(t.`PRE_NO`, '') AS PRE_NO, IFNULL(t.`INVT_NO`, '') AS INVT_NO, IFNULL(orders.`EBP_CODE`, '') AS EBP_CODE, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'EBP_CODE' AND c.CODE = orders.EBP_CODE) AS EBP_NAME, IFNULL(t.`LOGISTICS_NO`, '') AS LOGISTICS_NO, IFNULL(logistics.`LOGISTICS_CODE`, '') AS LOGISTICS_CODE, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'LOGISTICS_CODE' AND c.CODE = logistics.LOGISTICS_CODE) AS LOGISTICS_NAME, IFNULL(orders.`ORDER_TYPE`, '') AS BIZ_TYPE, IFNULL(t.`PORT_CODE`, '') AS PORT_CODE, IFNULL(DATE_FORMAT(t.`IE_DATE`,'%Y%m%d'), '') AS IE_DATE, IFNULL(t.`OWNER_CODE`, '') AS OWNER_CODE, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'EBC_CODE' AND c.CODE = t.OWNER_CODE) AS OWNER_NAME, IFNULL(orders.`EBC_CODE`, '') AS TRADE_CODE, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'EBC_CODE' AND c.CODE = orders.EBC_CODE) AS TRADE_NAME, IFNULL(orders.`AGENT_CODE`, '') AS AGENT_CODE, (SELECT c.name FROM t_code_name c WHERE c.RELATION_CATEGORY = 'AGENT_CODE' AND c.CODE = orders.AGENT_CODE) AS AGENT_NAME, IFNULL(t.`TRADE_MODE`, '') AS TRADE_MODE, IFNULL(logistics.`TRAF_MODE`, '') AS TRAF_MODE, IFNULL(logistics.`TRAF_NAME`, '') AS TRAF_NAME, IFNULL(logistics.`VOYAGE_NO`, '') AS VOYAGE_NO, IFNULL(logistics.`BILL_NO`, '') AS BILL_NO, IFNULL(logistics.`MAIN_BILL_NO`, '') AS MAIN_BILL_NO, IFNULL(t.`LOCT_NO`, '') AS LOCT_NO, IFNULL(t.`LICENSE_NO`, '') AS LICENSE_NO, IFNULL(t.`COUNTRY`, '') AS COUNTRY, IFNULL(t.`DESTINATION_PORT`, '') AS DESTINATION_PORT, IFNULL(orders.`GOODS_VALUE`, '') AS GOODS_VALUE, IFNULL(orders.`TAX_FEE`, '') AS TAX_FEE, IFNULL(orders.`CURRENCY`, '') AS GOODS_CURRENCY, IFNULL(t.`FREIGHT`, '') AS FREIGHT, IFNULL(t.`FREIGHT_CURR`, '') AS FREIGHT_CURR, IFNULL(t.`FREIGHT_MARK`, '') AS FREIGHT_MARK, IFNULL(t.`INSURE_FEE`, '') AS INSURE_FEE, IFNULL(t.`INSURE_FEE_CURR`, '') AS INSURE_FEE_CURR, IFNULL(t.`INSURE_FEE_MARK`, '') AS INSURE_FEE_MARK, IFNULL(t.`WRAP_TYPE`, '') AS WRAP_TYPE, IFNULL(t.`PACK_NO`, '') AS PACK_NO, IFNULL(t.`WEIGHT`, '') AS WEIGHT, IFNULL(t.`NET_WEIGHT`, '') AS NET_WEIGHT_SP, IFNULL(t.`NOTE`, '') AS NOTE,
logistics.`IE_FLAG` AS IE_FLAG, CODENAME.`CODE` AS CODE_CODE, CODENAME.`NAME` AS CODE_NAME, CONTACT.`CODE` AS CONTACT_CODE, CONTACT.`NAME` AS CONTACT_NAME
FROM T_import_INVENTORY t LEFT JOIN T_import_LOGISTICS logistics ON t.LOGISTICS_NO = logistics.LOGISTICS_NO LEFT JOIN t_import_orders orders ON orders.ORDER_NO = logistics.ORDER_NO LEFT JOIN `T_CODE_NAME` CODENAME ON logistics.LOGISTICS_CODE = CODENAME.CODE AND CODENAME.RELATION_CATEGORY="LOGISTICS_CODE" LEFT JOIN `T_CONTACT` CONTACT ON logistics.CONSIGNEE_ID = CONTACT.CONTACT_ID WHERE t.`GUID` = #{guid}
